import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Swipeable from "react-swipy";
import { fetchRecommendations } from "../scripts/APIscript";
import Loading from "../compontents/loading";

const Recommendations = ({ accessToken }) => {
  const [data, setData] = useState(undefined);

  useEffect(() => {
    getRecommendations();
  }, [accessToken]);

  function getRecommendations() {
    if (accessToken) {
      fetchRecommendations(accessToken).then((data) => {
        setData(data.tracks);
      });
    }
  }


  const CardDeck = () => {
    let index = 0;
    return (
      <div className="wrapperRec">
        {data ? (
          <Swipeable
            buttons={({ right, left, back }) => (
              <div className="actions d-flex justify-content-between">
                <button className="btn btn-outline-danger rounded-circle" onClick={left}>
                  <i className="bi bi-x-lg"></i>
                </button>
                <button className="btn btn-outline-warning rounded-circle" onClick={back}>
                <i className="bi bi-rewind-fill"></i>
                </button>
                <button className="btn btn-outline-success rounded-circle" onClick={right}>
                  <i className="bi bi-plus-lg"></i>
                </button>
              </div>
            )}
            onAfterSwipe={() => (index += 1)}
          >
            <div className="card swipeable-cards m-auto text-light position-relative">
              <img
                className="card-img-top"
                src={data[index].album.images[1].url}
                alt="Card image cap"
              />
              <div className="card-body">
                <h3 className="card-title">{data[index].name}</h3>
                <h5>{data[index].artists[0].name}</h5>
                <p className="card-text my-3">
                  <i className="bi bi-vinyl-fill"></i> {data[index].album.name}
                </p>
              </div>
            </div>
          </Swipeable>
        ) : (
          <Loading></Loading>
        )}
      </div>
    );
  };

  const TrackList = () => {
    return (
      <div className="container mt-5">
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {data.map((track) => (
            <div className="col" key={track.id}>
              <div className="card h-100 bg-dark text-light border-light text-center">
                <img
                  src={track.album.images[1].url}
                  className="card-img-top w-50 mx-auto"
                  alt={track.album.name}
                />
                <div className="card-body">
                  <h5 className="card-title">{track.name}</h5>
                  <p className="card-text">
                    <strong>Artist:</strong>{" "}
                    <a
                      href={track.artists[0].external_urls.spotify}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      {track.artists[0].name}
                    </a>
                  </p>
                  <p className="card-text">
                    <strong>Album:</strong>{" "}
                    <a
                      href={track.album.external_urls.spotify}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      {track.album.name}
                    </a>
                  </p>
                </div>
                <div className="card-footer ">
                  <a
                    href={track.external_urls.spotify}
                    className="btn btn-success"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Listen on Spotify
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    /*<div>
      <h1 className="text-center mt-5">Recommended Tracks</h1>

      {data ? (
        <>
          <div className="text-center">
            <button
              className="btn btn-success my-5"
              onClick={getRecommendations}
            >
              {" "}
              Reload
            </button>
          </div>
          <TrackList />
        </>
      ) : (
        <div className="d-flex justify-content-center align-items-center h-50">
          <div className="spinner-border text-success" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      )}
    </div>*/

    <div>
      {data ? (
        <>
          <div className="mx-5">
            <CardDeck></CardDeck>
          </div>
        </>
      ) : (
        <></>
      )}
    </div>
  );
};

export default Recommendations;
