import React, { useEffect, useState } from "react";
import { fetchRecommendations } from "./scripts/APIscript";
import { Card, CardContainer } from "react-swipy";

const Recommendations = ({ accessToken }) => {
  const [data, setData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    getRecommendations();
  }, [accessToken]);

  function getRecommendations() {
    if (accessToken) {
      fetchRecommendations(accessToken).then((data) => {
        setData(data.tracks);
      });
    }
  }

  const handleSwipe = (direction) => {
    // Handle swipe action (left or right)
    // For example: console.log(`Swiped ${direction}`);
    setCurrentIndex(currentIndex + 1);
  };

  const renderCard = (track, index) => {
    return (
      <Card key={track.id} onSwipe={handleSwipe}>
        <div className="card bg-dark text-light border-light text-center">
          <img
            src={track.album.images[1].url}
            className="card-img-top w-50 mx-auto"
            alt={track.album.name}
          />
          <div className="card-body">
            <h5 className="card-title">{track.name}</h5>
            <p className="card-text">
              <strong>Artist:</strong>{" "}
              <a
                href={track.artists[0].external_urls.spotify}
                target="_blank"
                rel="noopener noreferrer"
              >
                {track.artists[0].name}
              </a>
            </p>
            <p className="card-text">
              <strong>Album:</strong>{" "}
              <a
                href={track.album.external_urls.spotify}
                target="_blank"
                rel="noopener noreferrer"
              >
                {track.album.name}
              </a>
            </p>
          </div>
          <div className="card-footer">
            <a
              href={track.external_urls.spotify}
              className="btn btn-success"
              target="_blank"
              rel="noopener noreferrer"
            >
              Listen on Spotify
            </a>
          </div>
        </div>
      </Card>
    );
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mt-5">Recommended Tracks</h1>
      <div className="text-center">
        <button className="btn btn-success my-5" onClick={getRecommendations}>
          Reload
        </button>
      </div>
      <CardContainer className="swipeable-cards">
        {data.slice(currentIndex).map((track, index) =>
          renderCard(track, index)
        )}
      </CardContainer>
    </div>
  );
};

export default Recommendations;
