export async function fetchProfile(code: string): Promise<UserProfile> {
  const result = await fetch("https://api.spotify.com/v1/me", {
    method: "GET",
    headers: { Authorization: `Bearer ${code}` },
  });

  return await result.json();
}

export async function fetchArtists(
  code: string,
  termIndex: number
): Promise<any> {
  const terms = ["short_term", "medium_term", "long_term"];
  const result = await fetch(
    `https://api.spotify.com/v1/me/top/artists?time_range=${terms[termIndex]}&limit=50`,
    {
      method: "GET",
      headers: {
        Authorization: `Bearer ${code}`,
      },
    }
  );

  return await result.json();
}

export async function fetchTracks(
  code: string,
  termIndex: number
): Promise<any> {
  const terms = ["short_term", "medium_term", "long_term"];
  const result = await fetch(
    `https://api.spotify.com/v1/me/top/tracks?time_range=${terms[termIndex]}&limit=50`,
    {
      method: "GET",
      headers: { Authorization: `Bearer ${code}` },
    }
  );

  return await result.json();
}

export async function fetchAvalibleGenres(code: string): Promise<any> {
  const result = await fetch(
    `https://api.spotify.com/v1/recommendations/available-genre-seeds`,
    {
      method: "GET",
      headers: { Authorization: `Bearer ${code}` },
    }
  );

  return await result.json();
}

export async function fetchRecommendations(code: string): Promise<any> {
  const artists = await fetchArtists(code, 2);
  const artistIds = artists.items.slice(0, 5).map((artist: any) => artist.id);
  const seed_artists = artistIds.join(",");

  const genres = artists.items
    .slice(0, 5)
    .map((artist: { genres: string[] }) => artist.genres)
    .reduce((acc, genres) => acc.concat(genres), []);
  // Remove duplicates by converting to a Set and then back to an array
  const uniqueGenres = Array.from(new Set(genres));

  // Fetch available genres
  const availableGenres = await fetchAvalibleGenres(code);

  // Filter unique genres based on available genres
  const filteredGenres = uniqueGenres.filter((genre) =>
    availableGenres.genres.includes(genre)
  );

  // Limit the number of genres to 5
  const limitedGenres = filteredGenres.slice(0, 5);
  // Join genres into a single comma-separated string
  const seed_genres = limitedGenres.join(",");

  console.log(seed_genres);

  const tracks = await fetchTracks(code, 1);
  const randomIndex = Math.floor(Math.random() * 20);
  const seed_track = tracks.items[randomIndex].id;

  const result = await fetch(
    `https://api.spotify.com/v1/recommendations?seed_artists=4NHQUGzhtTLFvgF5SZesLK&seed_genres=classical&seed_tracks=0c6xIDDpzE81m2q797ordA`,
    {
      method: "GET",
      headers: { Authorization: `Bearer ${code}` },
    }
  );

  return await result.json();
}
