import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "bootstrap/dist/js/bootstrap.min.js";
import "./App.css";

import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import {
  isAccessTokenExpired,
  redirectToAuthCodeFlow,
  clientId,
} from "./scripts/authCodeWithPkce";
import { fetchProfile } from "./scripts/APIscript";

import Layout from "./compontents/layout";
import Artists from "./pages/artists";
import Home from "./pages/home";
import Songs from "./pages/songs";
import NotFound from "./pages/notFound";
import Callback from "./pages/callback";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [profile, setProfile] = useState(null);

  const signIn = () => {
    redirectToAuthCodeFlow(clientId)
      .then(() => {
        const newAccessToken = localStorage.getItem("accessToken"); // fetch updated token
        const profile = fetchProfile(newAccessToken);
        console.log(profile)
        setProfile(profile);
        setIsAuthenticated(true);
      })
      .catch(error => {
        // Handle any errors that occurred during the sign-in process
        console.error("Error during sign-in:", error);
      });
  };

  const signOut = () => {
    localStorage.clear();
    setProfile(null);
    setIsAuthenticated(false);
  };

  useEffect(() => {
    /*const accessToken = localStorage.getItem("accessToken");
    if (isAccessTokenExpired(accessToken)) {
      console.log("Error fetching profile");
      signOut();
    }*/

  }, []);

  return (
    <Router>
      <Layout authStatus={{ isAuthenticated, profile, signIn, signOut }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/artists" element={<Artists />} />
          <Route path="/songs" element={<Songs />} />
          <Route path="/callback" element={<Callback />} />
          <Route element={<NotFound />} />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
