import React, { useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

function Callback() {
    useEffect(() => {
        const params = new URLSearchParams(window.location.search);
        sessionStorage.setItem("code", params.get("code"));

        const referrer = document.referrer;
        const origin = window.location.origin;

        // Check if the referrer is from the same origin
        if (referrer.startsWith(origin)) {
            // Navigate to the previous page in the browsing history
            window.history.back();
        } else {
            // Redirect to the homepage if the referrer is not from the same origin
            window.location.href = '/';
        }
    }, []);

    return (
        <div className="d-flex justify-content-center align-items-center vh-100">
            <div className="spinner-border text-success" role="status">
                <span className="visually-hidden">Loading...</span>
            </div>
        </div>
    );
}

export default Callback;
