import React, { useEffect } from 'react';
import { clientId, getAccessToken } from '../scripts/authCodeWithPkce';

function Callback({isAuthenticated, profile}) {
    useEffect(() => {
        const fetchAccessToken = async () => {
            console.log("callback")
            const params = new URLSearchParams(window.location.search);
            const code = params.get("code");
            const accessToken = await getAccessToken(clientId, code);
            localStorage.setItem("accessToken", accessToken);
            localStorage.setItem("accessTokenTimestamp", new Date().toDateString());
            const referrer = document.referrer;
            const origin = window.location.origin;

            // Check if the referrer is from the same origin
            /*if (referrer.startsWith(origin)) {
