import { useEffect, useState } from "react";
import ArtistCard from "../compontents/artistCard";
import { fetchArtists } from "../scripts/APIscript";
import Loading from "../compontents/loading";

function Artists({ accessToken }) {
  const [data, setData] = useState(undefined);

  useEffect(() => {
    getArtists();
  }, [accessToken]);

  function getArtists() {
    if (accessToken) {
      fetchArtists(accessToken).then((data) => {
        setData(data);
      });
    }
  }

  const ArtistList = () => {
    return (
      <>
        <ul className="nav nav-tabs m-auto w-75 mb-5 bg-dark">
          <li className="nav-item">
            <a
              className="nav-link active text-light bg-dark border-light"
              aria-current="page"
              href="#"
            >
              4 Weeks
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link text-light" href="#">
              6 Months
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link text-light" href="#">
              1 Year
            </a>
          </li>
        </ul>
        <div className="container">
          <div className="row row-cols-1 row-cols-md-3 g-5">
            {data?.items ? (
              data.items.map((artist) => (
                <ArtistCard key={artist.id} artist={artist} />
              ))
            ) : (
              <Loading></Loading>
            )}
          </div>
        </div>
      </>
    );
  };

  return (
    <>
      <ArtistList />
    </>
  );
}

export default Artists;
