import axios from "axios";
import { useEffect, useState } from "react";

function Artists() {
    const endPoint = "https://api.spotify.com/v1/me/top/artists";
    let count = 1;

    const [token,setToken] = useState("");
    const [data,setData] = useState(undefined);

    useEffect(() => {
        if(localStorage.getItem("accessToken")) {
            setToken(localStorage.getItem("accessToken"));
        }
    }, []);

    useEffect(() => {
        if (token) {
            getArtists();
        }
    }, [token]);

    const getArtists = () => {
        axios.get(endPoint, {
            headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
            setData(response.data);
        })
        .catch((error) => {
            console.log(error);
        })
    }

    const ArtistCard = ({artist }) => {
        return (
          <div className="col">
            <div className="card w-100 h-100 text-center bg-dark text-light border-0">
                <div className="text-center">
                <img src={artist.images[2].url} className="card-img-top t w-25" alt={artist.name} />
                </div>
              <div className="card-body">
                <h5 className="card-title">{count++}{". " + artist.name}</h5>
                <p className="card-text">
                  Followers: {artist.followers.total.toLocaleString()}<br />
                  Popularity: {artist.popularity}
                </p>
                <a href={artist.external_urls.spotify} className="btn btn-success" target="_blank" rel="noopener noreferrer">
                  Listen on Spotify
                </a>
              </div>
            </div>
          </div>
        );
      };

      const ArtistList = () => {
        return (
          <div className="container">
            <div className="row row-cols-1 row-cols-md-3 g-4">
              {data?.items ? data.items.map((artist) => (
                <ArtistCard key={artist.id} artist={artist} />
              )) : null}
            </div>
          </div>
        );
      };

    return (
        <>
            <header className="text-white text-center py-5">
        <div className="container">
          <h1 className="display-4">Top Artists</h1>
          <p className="lead">The Ultimate Music Data Experience</p>
        </div>
      </header>
        <ArtistList />
        </>
    )
}

export default Artists;
