import { redirectToAuthCodeFlow, getAccessToken } from "./authCodeWithPkce";

const clientId = "e5f9bfa9d40447488e4fc74d2c71d293";
const params = new URLSearchParams(window.location.search);
let code = params.get("code");

export async function checkAuth() {
    if (code){
        const accessToken = await getAccessToken(clientId, code);
        const profile = await fetchProfile(accessToken);
        sessionStorage.setItem("profile", JSON.stringify(profile));
        localStorage.setItem("accessToken", accessToken);
    }
}

export async function handleLogin() {
    if (!code) {
        redirectToAuthCodeFlow(clientId);
    }
}

export async function auth() {
    if (!code) {
        redirectToAuthCodeFlow(clientId);
    } else {
        const accessToken = await getAccessToken(clientId, code);
        const profile = await fetchProfile(accessToken);
    }
}

export function handleLogout() {
    localStorage.removeItem("verifier");
    localStorage.removeItem("access_token"); // If you store access token in localStorage
    sessionStorage.removeItem("profile")
    code = null;
    window.location.href = '/'; // Redirect to homepage or login page
}


export async function fetchFromSpotify(token: string, apiEndpoint: string): Promise<any> {
    const result = await fetch(apiEndpoint, {
        method: "GET", headers: { Authorization: `Bearer ${token}` }
    });

    return await result.json();
}
