import axios from "axios";
import { useEffect, useState } from "react";
import ArtistCard from "../compontents/artistCard";
import { fetchArtists } from "../scripts/APIscript";
function Artists({ accessToken }) {
  const [data, setData] = useState(undefined);

  useEffect(() => {
    getArtists();
  }, [accessToken]);

  function getArtists() {
    if (accessToken) {
      fetchArtists(accessToken).then((data) => {
        setData(data);
      });
    }
  }

  /*const ArtistCard = ({artist }) => {
        return (
          <div className="col">
            <div className="card w-100 h-100 text-center bg-dark text-light border-0">
                <div className="text-center">
                <img src={artist.images[2].url} className="card-img-top t w-25" alt={artist.name} />
                </div>
              <div className="card-body">
                <h5 className="card-title">{count++}{". " + artist.name}</h5>
                <p className="card-text">
                  Followers: {artist.followers.total.toLocaleString()}<br />
                  Popularity: {artist.popularity}
                </p>
                <a href={artist.external_urls.spotify} className="btn btn-success" target="_blank" rel="noopener noreferrer">
                  Listen on Spotify
                </a>
              </div>
            </div>
          </div>
        );
      };*/

  const ArtistList = () => {
    return (
      <div className="container">
        <div className="row row-cols-1 row-cols-md-3 g-4">
          {data?.items
            ? data.items.map((artist) => <ArtistCard artist={artist} />)
            : null}
        </div>
      </div>
    );
  };

  return (
    <>
      <header className="text-white text-center py-2">
        <div className="container">
          <h1 className="h3">Top Artists</h1>
        </div>
      </header>
      <ArtistList />
    </>
  );
}

export default Artists;
