export async function fetchProfile(code: string): Promise<UserProfile> {
  const result = await fetch("https://api.spotify.com/v1/me", {
    method: "GET",
    headers: { Authorization: `Bearer ${code}` },
  });

  return await result.json();
}

export async function fetchArtists(code: string): Promise<any> {
  const result = await fetch("https://api.spotify.com/v1/me/top/artists", {
    method: "GET",
    headers: { Authorization: `Bearer ${code}` },
  });

  return await result.json();
}

export async function fetchTracks(code: string): Promise<any> {
  const result = await fetch("https://api.spotify.com/v1/me/top/tracks", {
    method: "GET",
    headers: { Authorization: `Bearer ${code}` },
  });

  return await result.json();
}

export async function fetchRecommendations(code: string): Promise<any> {
  
  const artists = await (fetchArtists(code));
  const artistIds = artists.items.slice(0, 2).map((artist: any) => artist.id);
  const seed_artists = artistIds.join(',');

  const tracks = await (fetchTracks(code));
  const seed_tracks = tracks.items[5].id;


  const result = await fetch(`https://api.spotify.com/v1/recommendations?seed_artists=${seed_artists}&seed_genres=latino%2Chouse&seed_tracks=${seed_tracks}`, {
    method: "GET",
    headers: { Authorization: `Bearer ${code}` },
  });

  return await result.json();
}
