import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Swipeable from "react-swipy";
import { fetchRecommendations } from "../scripts/APIscript";
import Loading from "../compontents/loading";
import RecommentationCard from "../compontents/recommenationCard";

const Recommendations = ({ accessToken }) => {
  const [data, setData] = useState(undefined);
  const [index, setIndex] = useState(0);
  const [cards, setCards] = useState(["First", "Second", "Third"]);

  useEffect(() => {
    getRecommendations();
  }, [accessToken]);

  function getRecommendations() {
    if (accessToken) {
      fetchRecommendations(accessToken).then((data) => {
        setData(data.tracks);
      });
    }

    const remove = () => {
      setData((prevData) => prevData.slice(1)); // Using slice to remove the first card
    };
  }

  const wrapperStyles = {
    position: "relative",
    width: "250px",
    height: "250px",
  };
  const actionsStyles = {
    display: "flex",
    justifyContent: "space-between",
    marginTop: 12,
  };

  /*const CardDeck = () => {
    return (
      <div className="wrapper">
        {data ? (
          <Swipeable
            buttons={({ right, left, back }) => (
              <div className="actions d-flex justify-content-between">
                <button className="btn btn-outline-danger border border-danger border-3 rounded-circle action-btn" onClick={left}>
                  <i className="bi bi-x-lg fw-bold"></i>
                </button>
                <button className="btn btn-outline-warning border border-warning border-3 rounded-circle action-btn" onClick={back}>
                <i className="bi bi-rewind-fill fw-bold"></i>
                </button>
                <button className="btn btn-outline-success border border-success border-3 rounded-circle action-btn" onClick={right}>
                  <i className="bi bi-plus-lg "></i>
                </button>
              </div>
            )}
            onAfterSwipe={() => setIndex(index + 1)}
          >
            <div className="card swipeable-cards m-auto text-light position-relative">
              <img
                className="card-img-top"
                src={data[index].album.images[1].url}
                alt="Card image cap"
              />
              <div className="card-body">
                <h3 className="card-title">{data[index].name}</h3>
                <h5>{data[index].artists[0].name}</h5>
                <p className="card-text my-3">
                  <i className="bi bi-vinyl-fill"></i> {data[index].album.name}
                </p>
              </div>
            </div>
          </Swipeable>
        ) : (
          <Loading></Loading>
        )}
      </div>
    );
  };*/

  const CardDeck = () => {
    return (
      <div>
        <div style={wrapperStyles}>
          {cards.length > 0 ? (
            <div style={wrapperStyles}>
              <Swipeable
                buttons={({ left, right, back }) => (
                  <div style={actionsStyles}>
                    <button
                      className="btn btn-outline-danger border border-danger border-3 rounded-circle action-btn"
                      onClick={left}
                    >
                      <i className="bi bi-x-lg fw-bold"></i>
                    </button>
                    <button
                      className="btn btn-outline-warning border border-warning border-3 rounded-circle action-btn"
                      onClick={back}
                    >
                      <i className="bi bi-rewind-fill fw-bold"></i>
                    </button>
                    <button
                      className="btn btn-outline-success border border-success border-3 rounded-circle action-btn"
                      onClick={right}
                    >
                      <i className="bi bi-plus-lg "></i>
                    </button>
                  </div>
                )}
                onAfterSwipe={remove}
              >
                <RecommentationCard track>{cards[0]}</RecommentationCard>
              </Swipeable>
              {cards.length > 1 && <RecommentationCard zIndex={-1}>{cards[1]}</RecommentationCard>}
            </div>
          ) : (
            <RecommentationCard zIndex={-2}>No more cards</RecommentationCard>
          )}
        </div>
      </div>
    );
  };

  return (
    <div>
      {data ? (
        <>
          <div className="mx-5">
            <CardDeck></CardDeck>
          </div>
        </>
      ) : (
        <Loading></Loading>
      )}
    </div>
  );
};

export default Recommendations;
