import axios from "axios";
import { useEffect, useState } from "react";

function Artists() {
    const endPoint = "https://api.spotify.com/v1/me/top/artists";

    const [token,setToken] = useState("");
    const [data,setData] = useState([]);

    useEffect(() => {
        if(localStorage.getItem("accessToken")) {
            setToken(localStorage.getItem("accessToken"));
        }
    }, []);

    const getArtists = () => {
        axios.get(endPoint, {
            headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
            setData(response.data);
        })
        .catch((error) => {
            console.log(error);
        })
    }

    const ArtistCard = ({ artist }) => {
        return (
          <div className="col">
            <div className="card h-100">
              <img src={artist.images[0].url} className="card-img-top" alt={artist.name} />
              <div className="card-body">
                <h5 className="card-title">{artist.name}</h5>
                <p className="card-text">
                  Followers: {artist.followers.total.toLocaleString()}<br />
                  Popularity: {artist.popularity}<br />
                  Genres: {artist.genres.join(', ')}
                </p>
                <a href={artist.external_urls.spotify} className="btn btn-primary" target="_blank" rel="noopener noreferrer">
                  Listen on Spotify
                </a>
              </div>
            </div>
          </div>
        );
      };

      const ArtistList = () => {
        return (
          <div className="container">
            <div className="row row-cols-1 row-cols-md-3 g-4">
              {data.map((artist) => (
                <ArtistCard key={artist.id} artist={artist} />
              ))}
            </div>
          </div>
        );
      };

    return (
        <>
        <p className="text-light">Artists</p>
        <button onClick={getArtists}>Knabb</button>
        <ArtistList />
        </>
    )
}

export default Artists;
