import { useEffect, useState } from "react";
import ArtistCard from "../compontents/artistCard";
import { fetchArtists } from "../scripts/APIscript";
import { Link, useLocation } from 'react-router-dom';
import Loading from "../compontents/loading";

function Artists({ accessToken }) {
  const [data, setData] = useState(undefined);
  const location = useLocation();
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
    getArtists(index);
  };

  useEffect(() => {
    getArtists(0);
  }, [accessToken]);

  useEffect(() => {
    if (location.pathname.includes('/artists/4-weeks')) {
      setActiveTab(0);
    } else if (location.pathname.includes('/artists/6-months')) {
      setActiveTab(1);
    } else if (location.pathname.includes('/artists/1-year')) {
      setActiveTab(2);
    }
  }, [location]);

  function getArtists(termIndex) {
    if (accessToken) {
      fetchArtists(accessToken, termIndex).then((data) => {
        setData(data);
      });
    }
  }

  return (
    <>
      {data ? (
        <>
          <ul className="nav nav-tabs m-auto w-75 mb-5 bg-dark">
            <li className="nav-item">
              <Link
                className={`nav-link text-light ${activeTab === 0 ? 'active bg-dark border-light' : 'border-light'}`}
                aria-current="page"
                onClick={() => handleTabClick(0)}
                to="/artists/4-weeks"
              >
                4 Weeks
              </Link>
            </li>
            <li className="nav-item">
              <Link
                className={`nav-link text-light ${activeTab === 1 ? 'active bg-dark' : ''}`}
                onClick={() => handleTabClick(1)}
                to="/artists/6-months"
              >
                6 Months
              </Link>
            </li>
            <li className="nav-item">
              <Link
                className={`nav-link text-light ${activeTab === 2 ? 'active bg-dark' : ''}`}
                onClick={() => handleTabClick(2)}
                to="/artists/1-year"
              >
                1 Year
              </Link>
            </li>
          </ul>
          <ArtistList />
        </>
      ) : (
        <Loading />
      )}
    </>
  );
};

export default Artists;

const ArtistList = () => {
  return (
    <div className="container">
      <div className="row row-cols-1 row-cols-md-3 g-5">
        {data.items.map((artist) => (
          <ArtistCard key={artist.id} artist={artist} />
        ))}
      </div>
    </div>
  );
};
