import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "bootstrap/dist/js/bootstrap.min.js";
import "./App.css";

import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import {
  isAccessTokenExpired,
  redirectToAuthCodeFlow,
  clientId,
} from "./scripts/authCodeWithPkce";
import { fetchProfile } from "./scripts/APIscript";

import Layout from "./compontents/layout";
import Artists from "./pages/artists";
import Home from "./pages/home";
import Songs from "./pages/songs";
import NotFound from "./pages/notFound";
import Callback from "./pages/callback";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const storedAccessToken = localStorage.getItem("accessToken");

  const signIn = async () => {
    await redirectToAuthCodeFlow(clientId);
    const profile = await fetchProfile(storedAccessToken);
    localStorage.setItem("profile", JSON.stringify(profile));
    setIsAuthenticated(true);
    console.log(isAuthenticated)
  };

  const signOut = () => {
    localStorage.clear();
    setIsAuthenticated(false);
  };

  useEffect(() => {
    /*if (!isAccessTokenExpired(storedAccessToken)) {
      signOut();
      console.log("Access token expired or not available.");
    }*/

  }, []);

  return (
    <Router>
      <Layout authStatus={{ isAuthenticated, signIn, signOut }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/artists" element={<Artists />} />
          <Route path="/songs" element={<Songs />} />
          <Route path="/callback" element={<Callback />} />
          <Route element={<NotFound />} />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
