import {Link} from "react-router-dom";
function ArtistCard({ artist}) {
  return (
    <Link to={artist.external_urls.spotify}>
    <div className="card bg-transparent shadow text-white artist">
      <img className="card-img w-100 h-100 darken" src={artist.images[0].url} alt={artist.name} />
      <div className="card-img-overlay">
        <h3 className="card-title ms-3 my-3">{artist.name}</h3>
        <div className="bg-primary rounded-circle">
            <h5 className="card-title ms-3">{"#" + artist.popularity}</h5>
        </div>
        <p className="card-text">
          {artist.desc}
        </p>
      </div>
    </div>
    </Link>
  );
}

export default ArtistCard;
