import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { fetchRecommendations } from "../scripts/APIscript";

const Recommendations = ({ accessToken }) => {
  const [data, setData] = useState(undefined);

  useEffect(() => {
    if (accessToken) {
      fetchRecommendations(accessToken).then((tracks) => {
        setData(tracks);
      });
    }
  }, [accessToken]);

  return (
    <div className="container">
      <h3 className="text-center my-4">Recommendations</h3>
      <div className="row">
        {data.length > 0 ? (
          data.map((track, index) => (
            <div className="col-md-4 mb-4" key={index}>
              <div className="card">
                <img
                  src={track.album.images[0].url}
                  className="card-img-top"
                  alt={track.name}
                />
                <div className="card-body">
                  <h5 className="card-title">{track.name}</h5>
                  <p className="card-text">
                    Artist:{" "}
                    {track.artists.map((artist) => artist.name).join(", ")}
                  </p>
                  <p className="card-text">Album: {track.album.name}</p>
                  <a
                    href={track.external_urls.spotify}
                    className="btn btn-primary"
                  >
                    Listen on Spotify
                  </a>
                </div>
              </div>
            </div>
          ))
        ) : (
          <div className="col-12">
            <p className="text-center">No recommendations available.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Recommendations;
