import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
function ArtistCard({ artist }) {
const [counter, setCounter] = useState(1);

useEffect(() => {
  // Update counter whenever artist changes
  setCounter((prevCounter) => prevCounter + 1);
}, [artist]); // Dependency array ensures useEffect runs when artist changes
  return (
    <Link to={artist.external_urls.spotify}>
      <div className="card bg-transparent shadow text-white artist zoom">
        <img
          className="card-img darken artist-img"
          src={artist.images[0].url}
          alt={artist.name}
        />
        <div className="card-img-overlay d-flex flex-column justify-content-between mx-2 my-2">
          <div className="d-flex justify-content-between align-items-center">
            <h3 className="m-0">{artist.name}</h3>
            <div className="d-flex ranking bg-dark align-items-center">
              <h4 className="m-0">{"#" + counter}</h4>
            </div>
          </div>
          <ul className="list-unstyled d-flex flex-row flex-wrap">
            {artist.genres.map((genre) => (
              <li key={genre} className="card-text">
                <span className="badge fs-6 genre rounded-pill me-2 mb-2 text-capitalize">{genre}</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </Link>
  );
}

export default ArtistCard;
