import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Swipeable from "react-swipy";
import { fetchRecommendations } from "../scripts/APIscript";
import Loading from "../compontents/loading";
import RecommentationCard from "../compontents/recommenationCard";

const Recommendations = ({ accessToken }) => {
  const [data, setData] = useState(undefined);
  const [index, setIndex] = useState(0);
  const [cards, setCards] = useState(["First", "Second", "Third"]);

  useEffect(() => {
    getRecommendations();
  }, [accessToken]);

  function getRecommendations() {
    if (accessToken) {
      fetchRecommendations(accessToken).then((data) => {
        setData(data.tracks);
      });
    }
    console.log(data)
  }

  function remove() {
    setData((prevData) => prevData.slice(1)); // Using slice to remove the first card
  }

  const wrapperStyles = {
    position: "relative",
    width: "250px",
    height: "250px",
  };
  const actionsStyles = {
    display: "flex",
    justifyContent: "space-between",
    marginTop: 12,
  };

  const CardDeck = () => {
    return (
      <div>
        <div style={wrapperStyles}>
          {data ? (
            <div style={wrapperStyles}>
              <Swipeable
                buttons={({ left, right, back }) => (
                  <div style={actionsStyles}>
                    <button
                      className="btn btn-outline-danger border border-danger border-3 rounded-circle action-btn"
                      onClick={left}
                    >
                      <i className="bi bi-x-lg fw-bold"></i>
                    </button>
                    <button
                      className="btn btn-outline-warning border border-warning border-3 rounded-circle action-btn"
                      onClick={back}
                    >
                      <i className="bi bi-rewind-fill fw-bold"></i>
                    </button>
                    <button
                      className="btn btn-outline-success border border-success border-3 rounded-circle action-btn"
                      onClick={right}
                    >
                      <i className="bi bi-plus-lg "></i>
                    </button>
                  </div>
                )}
                onAfterSwipe={remove()}
              >
                <RecommentationCard track={data[0]}></RecommentationCard>
              </Swipeable>
              {cards.length > 1 && (
                <RecommentationCard
                  track={data[1]}
                  zIndex={-1}
                ></RecommentationCard>
              )}
            </div>
          ) : (
            <Loading></Loading>
          )}
        </div>
      </div>
    );
  };

  return (
    <div>
      {data ? (
        <>
          <div className="mx-5">
            <CardDeck></CardDeck>
          </div>
        </>
      ) : (
        <Loading></Loading>
      )}
    </div>
  );
};

export default Recommendations;
