import { Link } from "react-router-dom";
function TrackCard({ track}) {
  return (
    <Link to={track.external_urls.spotify}>
      <div className="card bg-transparent shadow text-white artist">
        <img
          className="card-img darken artist-img"
          src={track.album.images[0].url}
          alt={track.name}
        />
        <div className="card-img-overlay d-flex flex-column justify-content-between">
          <div className="d-flex justify-content-between align-items-center mx-2 my-2">
            <div>
            <h3 className="m-0 mb-1">{track.name}</h3>
            <h5 className="m-0">{track.artists.map(artist => artist.name).join(', ')}</h5>
            </div>

            <div className="d-flex ranking rounded-circle align-items-center">
              <h4 className="m-0">{"#" + track.popularity}</h4>
            </div>
          </div>
          {/*<ul className="list-unstyled d-flex flex-row flex-wrap">
            {track.artists[0].genres.map((genre) => (
              <li key={genre} className="card-text">
                <span className="badge fs-6 genre rounded-pill me-2 mb-2 text-capitalize">{genre}</span>
              </li>
            ))}
          </ul>*/}
        </div>
      </div>
    </Link>
  );
}

export default TrackCard;
