import React, { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap-icons/font/bootstrap-icons.min.css";
import "bootstrap/dist/js/bootstrap.min.js";
import "bootstrap/dist/js/bootstrap.min.js";
import "./App.css";

import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { isAccessTokenExpired, redirectToAuthCodeFlow,} from './scripts/authCodeWithPkce';
import { fetchProfile } from './scripts/APIscript';

import Layout from "./compontents/layout";
import Artists from "./pages/artists";
import Home from "./pages/home";
import Songs from "./pages/songs"
import NotFound from "./pages/notFound";
import Callback from "./pages/callback";


function App() {

  const [callbackComplete, setCallbackComplete] = useState(false);

  useEffect(() => {
    if (callbackComplete) {
      const fetchData = async () => {
        const accessToken = localStorage.getItem("accessToken");
        if (isAccessTokenExpired(accessToken)) {
          localStorage.clear();
          // Redirect or handle expired token situation
          console.log("Access token expired or not available.");
          return;
        }

        try {
          const profile = await fetchProfile(accessToken);
          localStorage.setItem("profile", JSON.stringify(profile));
        } catch (error) {
          console.error("Error fetching profile:", error);
          // Handle error gracefully, e.g., clear localStorage if necessary
        }
      };

      fetchData();
    }
  }, [callbackComplete]);

  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<Home/>} />
          <Route path="/artists" element={<Artists/>} />
          <Route path="/songs" element={<Songs/>} />
          <Route
            path="/callback"
            element={<Callback onCallbackComplete={() => setCallbackComplete(true)} />}
          />
          <Route element={<NotFound/>} />
        </Routes>
      </Layout>
    </Router>
  );
};

export default App;
